import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-eu',
  templateUrl: './eu.component.html',
  styleUrls: ['./eu.component.css']
})
export class EuComponent implements OnInit {
  constructor() { }

  ngOnInit() {
    this.typeAnimation();
  }

  typeAnimation() {
    const textElement = document.getElementById("animated-text");
    const subtextElement = document.getElementById("animated-subtext");

    const text = "Olá, Bem vindo a AllCortex";
    const subtext = "Especializada em front end";

    let index = 0;
    let subindex = 0;

    function type() {
      if (index < text.length) {
        textElement!.textContent += text.charAt(index);
        index++;
        setTimeout(type, 100);
      } else if (subindex < subtext.length) {
        subtextElement!.style.opacity = "1";
        subtextElement!.textContent += subtext.charAt(subindex);
        subindex++;
        setTimeout(type, 100);
      }
    }

    type();
  }
}
<div class="p-grid p-align-center p-justify-center about-me">
  <div class="p-col-12 p-md-6">
   <div class="img">
    <img src="./assets/avatar.png"  alt="Profile Photo" class="profile-photo">
   </div> 
  </div>
  <div class="p-col-12 p-md-6">
    <h2 id="animated-text"></h2>
    <p id="animated-subtext"></p>
  </div>
</div>
