import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-projects',
  templateUrl: './projects.component.html',
  styleUrls: ['./projects.component.css']
})
export class ProjectsComponent implements OnInit {
  projects: any[] = [];

  constructor() { }

  ngOnInit(): void {
    this.projects = [
      {
        name: 'Portfolio Website',
        description: 'A modern and clean portfolio website built with Angular, PrimeNG, and PrimeIcons.',
        image: 'assets/images/portfolio-website.png'
      },
      {
        name: 'Project 2',
        description: 'Description for Project 2',
        image: 'assets/images/project2.png'
      },
      {
        name: 'Project 3',
        description: 'Description for Project 3',
        image: 'assets/images/project3.png'
      }
    ];
  }
}
<p-carousel [value]="projects" [numVisible]="3" [numScroll]="3">
  <ng-template let-project pTemplate="item">
    <p-card [header]="project.name" [subheader]="project.description">
      <img src="{{ project.image }}" alt="{{ project.name }}">
    </p-card>
  </ng-template>
</p-carousel>
