import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-conhecimentos',
  templateUrl: './conhecimentos.component.html',
  styleUrls: ['./conhecimentos.component.css']
})
export class ConhecimentosComponent implements OnInit {
  ngOnInit(): void {
    throw new Error('Method not implemented.');
  }

  technologies = [
    { name: 'Angular', icon: 'assets/icons/angular.svg' },
    { name: 'React', icon: 'assets/icons/react.svg' },
    { name: 'Node.js', icon: 'assets/icons/nodejs.svg' },
    { name: 'Python', icon: 'assets/icons/python.svg' },
    { name: 'AWS', icon: 'assets/icons/aws.svg' },
    { name: 'Azure', icon: 'assets/icons/azure.svg' },
    { name: 'Ruby on Rails', icon: 'assets/icons/rails.svg' },
    { name: 'VueJS', icon: 'assets/icons/vuejs.svg' },
    { name: 'Elixir', icon: 'assets/icons/elixir.svg' },
    { name: 'Django', icon: 'assets/icons/django.svg' },
    { name: 'Figma', icon: 'assets/icons/figma.svg' },
    { name: 'Flutter', icon: 'assets/icons/flutter.svg' }
  ];

}
<div class="tech-showcase">
  <h2>Nossas principais tecnologias</h2>
  <div class="grid grid-nogutter">
    <div class="col-12 md:col-2" *ngFor="let tech of technologies">
      <div class="p-card p-component p-shadow-2 tech-card">
        <img [src]="tech.icon" [alt]="tech.name" class="tech-icon" />
        <span>{{ tech.name }}</span>
      </div>
    </div>
  </div>
</div>
