import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-projects',
  templateUrl: './projects.component.html',
  styleUrls: ['./projects.component.css']
})
export class ProjectsComponent implements OnInit {

  projects: any[] = [];
  selectedProject: any = null;

  ngOnInit(): void {
    this.projects = [
      {
        name: 'TaskMaster Pro',
        description: 'An advanced task management app for teams.',
        image: './assets/img1.webp'
      },
      {
        name: 'FitLife Tracker',
        description: 'A health and fitness tracking app to help users achieve their goals.',
        image: './assets/img1.webp'
      },
      {
        name: 'ShopEase E-commerce',
        description: 'An easy-to-use e-commerce platform for small businesses.',
        image: './assets/img1.webp'
      },
      {
        name: 'TechHub',
        description: 'A modern technology forum for discussing the latest trends.',
        image: './assets/img1.webp'
      },
      {
        name: 'EduLearn',
        description: 'An interactive e-learning platform for online courses.',
        image: './assets/img1.webp'
      }
    ];
  }

  selectProject(project: any): void {
    this.selectedProject = project;
    console.log('Selected Project:', this.selectedProject);
  }
}
