import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-sobreMim',
  templateUrl: './sobreMim.component.html',
  styleUrls: ['./sobreMim.component.css']
})
export class SobreMimComponent  {
  displayMoreInfo: boolean = false;
  selectedMoreInfo: any = null;

  moreInfoSections = [
    {
      title: 'Formação Acadêmica',
      content: 'Atualmente, estou cursando o Superior de Tecnologia em Análise e Desenvolvimento de Sistemas na área de TI.'
    },
    {
      title: 'Experiência Profissional',
      content: 'Tenho experiência como técnico de hardware e sou estagiário na UPS Quality, onde estou adquirindo habilidades práticas que complementam minha formação.'
    },
    {
      title: 'Habilidades e Projetos',
      content: 'Possuo habilidades em HTML5, CSS3, Javascript e Angular. Mantenho um site curricular e já desenvolvi e vendi dois projetos adicionais.'
    },
    {
      title: 'Qualidades Pessoais',
      content: 'Sou uma pessoa comunicativa, proativa, com facilidade em trabalhar em equipe e excelente relacionamento interpessoal.'
    }
  ];

  selectMoreInfo(infoSection: any) {
    this.selectedMoreInfo = infoSection;
  }

  closeMoreInfoPopup() {
    this.selectedMoreInfo = null;
  }
}
