import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-eu',
  templateUrl: './eu.component.html',
  styleUrls: ['./eu.component.css']
})
export class EuComponent implements OnInit {
  constructor() { }

  ngOnInit() {
    this.typeAnimation();
  }

  typeAnimation() {
    const textElement = document.getElementById("animated-text");
    const subtextElement = document.getElementById("animated-subtext");

    const text = "Olá, meu nome é Guilherme Carvalho!!";
    const subtext = "Seja bem vindo ao meu portfólio";

    let index = 0;
    let subindex = 0;

    function type() {
      if (index < text.length) {
        textElement!.textContent += text.charAt(index);
        index++;
        setTimeout(type, 100);
      } else if (subindex < subtext.length) {
        subtextElement!.style.opacity = "1";
        subtextElement!.textContent += subtext.charAt(subindex);
        subindex++;
        setTimeout(type, 100);
      }
    }

    type();
  }
  downloadCV() {
    const link = document.createElement('a');
    link.href = './assets/2020-2022  Ensino Médio completo_20240818_164537_0000.pdf';
    link.download = 'Curriculo_Guilherme_Carvalho.pdf'; // Nome que aparecerá para o usuário no momento do download
    link.click();
  }

}
<section class="about-me">
  <div class="intro-text">
    <h2 class="typewriter">Olá, meu nome é Guilherme Carvalho!</h2>
    <p>Seja bem-vindo ao meu portfólio Front-end.</p>
    <div class="button-group">
      <button pButton type="button" label="Baixar Currículo" icon="pi pi-download" (click)="downloadCV()" class="p-button-secondary"></button>
      <a href="https://github.com/Guilherme645" target="_blank">
        <button pButton type="button" label="GitHub" icon="pi pi-github" class="p-button-github p-button-secondary"></button>
      </a>  
    </div>
  </div>
  <div class="img">
    <img src="./assets/perfil (2).png" alt="Profile Photo" class="profile-photo">
  </div>
</section>

<!-- Botão Flutuante do WhatsApp -->
<div class="floating-whatsapp">
  <a href="https://wa.me/+5561984264979" target="_blank" class="whatsapp-button">
    <i class="pi pi-whatsapp"></i>
  </a>
</div>
